tinymce.PluginManager.add("Twocolumns",function(e){e.addButton("Twocolumns",{text:"Two columns",icon:!1,onclick:function(){e.windowManager.open({title:"Two columns",body:[{type:"textbox",multiline:true,minHeight:200,name:"content1",label:"First column"},{type:"textbox",multiline:true,minHeight:200,name:"content2",label:"Second column"}],onsubmit:function(t){e.insertContent("<div class='flex_column av_one_half first'><p>"+t.data.content1+"</p></div><div class='flex_column av_one_half'><p>"+t.data.content2+"</p></div>")}})}})});